const App = () => {
  const course = 'Half Stack application development'
  // const part1 = 'Fundamentals of React'
  // const exercises1 = 10
  // const part2 = 'Using props to pass data'
  // const exercises2 = 7
  // const part3 = 'State of a component'
  // const exercises3 = 14

  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }

  const part2 = {
    name: 'Using props to pass',
    exercises: 7
  }

  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header course={course}></Header>
      <Content part1={part1} part2={part2} part3={part3}></Content>
      <Total exercises={part1.exercises+part2.exercises+part3.exercises}></Total>
    </div>
  )
}

const Header = (props) => {
  return <h1>{props.course}</h1>
}

const Content = (props) => {
  console.log(props)
  return (
  <div>
    <Part part={props.part1.name} exercises={props.part1.exercises}></Part>
    <Part part={props.part2.name} exercises={props.part2.exercises}></Part>
    <Part part={props.part3.name} exercises={props.part3.exercises}></Part>
  </div>
  )
}

const Part = (props) => {
  return (
    <p>
      {props.part} {props.exercises}
    </p> 
  )
}

const Total = (props) => {
  return (
    <>
     <p>Number of exercises {props.exercises}</p>
    </>
  )
}

export default App
