const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header course={course}></Header>
      <Content part={part1} exercises={exercises1}></Content>
      <Content part={part2} exercises={exercises2}></Content>
      <Content part={part3} exercises={exercises3}></Content>
      <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>
  )
}

//renders name of the course
const Header = (props) => {
  return (
    <>
      <h1>{props.course}</h1>
    </>
  )
}


//renders parts and their number of exercises
const Content = (props) => {
  <>
    <p> 
     {props.part} {props.exercises}
    </p>
  </>
}

