const App = () => {
  const course = 'Half Stack application development'

  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

const Header = (props) => {
  return <h1>{props.course}</h1>
}

const Content = (props) => {
  console.log(props)
  return (
  <div>
    <Part part={props[0].name} exercises={props[0].exercises}></Part>
    <Part part={props[0].name} exercises={props[0].exercises}></Part>
    <Part part={props[0].name} exercises={props[0].exercises}></Part>
  </div>
  )
}

const Part = (props) => {
  return (
    <p>
      {props.part} {props.exercises}
    </p> 
  )
}

const Total = (props) => {
  return (
    <>
     <p>Number of exercises {props.exercises}</p>
    </>
  )
}

export default App
